targets.expr:
sum(
    node_namespace_pod_container:container_cpu_usage_seconds_total:sum_irate{container="fpm", pod=~"$pod", namespace="$namespace"}
) by (pod)
/sum(
    kube_pod_container_resource_limits{container="fpm", pod=~"$pod", resource="cpu", namespace="$namespace"}
) by (pod)

sum(
    container_memory_working_set_bytes{container="fpm", pod=~"$pod", namespace="$namespace"}
) by (pod)
/sum(
    kube_pod_container_resource_limits{container="fpm", pod=~"$pod", namespace="$namespace"}
) by (pod)
